$(function() {
    $("body").on("click", "a.page-scroll", function(a) {
        var b = $(this);
        $("html, body").stop().animate({
            scrollTop: $(b.attr("href")).offset().top
        }, 1500, "easeInOutExpo"), a.preventDefault()
    })
}), $(function() {
    $("body").on("input propertychange", ".floating-label-form-group", function(a) {
        $(this).toggleClass("floating-label-form-group-with-value", !!$(a.target).val())
    }).on("focus", ".floating-label-form-group", function() {
        $(this).addClass("floating-label-form-group-with-focus")
    }).on("blur", ".floating-label-form-group", function() {
        $(this).removeClass("floating-label-form-group-with-focus")
    })
}), $("body").scrollspy({
    target: ".navbar-fixed-top"
}), $(".navbar-collapse ul li a").click(function() {
    $(".navbar-toggle:visible").click()
}), $('#ibm-logo').mouseover(function() {
    $('#ibm-logo').attr('src', '/img/sponsors/ibm.png')
}), $('#ibm-logo').mouseout(function() {
    $('#ibm-logo').attr('src', '/img/sponsors/ibm-bw.png')
}), $('#create-logo').mouseover(function() {
    $('#create-logo').attr('src', '/img/sponsors/create-logo.png')
}), $('#create-logo').mouseout(function() {
    $('#create-logo').attr('src', '/img/sponsors/create-logo-bw.png')
}), $(window).on('scroll', function() {
    var scrollPos = window.pageYOffset;

    if (scrollPos > $(window).height()) {
        if (!$('.navbar').hasClass('expand')) {
            $('.navbar').addClass('expand');
        }
    } else {
        if ($('.navbar').hasClass('expand')) {
            $('.navbar').removeClass('expand');
        }
    }
}), $(document).on("click touchstart", ".sidebar-brand", function(e) {
    $("html, body").animate({
        scrollTop: $('#top').offset().top
    }, 1000, "easeInOutExpo");
}), $(document).on("click touchstart", "#li-gen", function(e) {
    $("html, body").animate({
        scrollTop: $('#general').offset().top
    }, 1000, "easeInOutExpo");
}), $(document).on("click touchstart", "#li-env", function(e) {
    $("html, body").animate({
        scrollTop: $('#smart-env').offset().top
    }, 1000, "easeInOutExpo");
}), $(document).on("click touchstart", "#li-vr", function(e) {
    $("html, body").animate({
        scrollTop: $('#vr').offset().top
    }, 1000, "easeInOutExpo");
}), $(document).on("click touchstart", "#li-fin", function(e) {
    $("html, body").animate({
        scrollTop: $('#fin-tech').offset().top
    }, 1000, "easeInOutExpo");
}), $(document).on("click touchstart", "#li-apps", function(e) {
    $("html, body").animate({
        scrollTop: $('#social-apps').offset().top
    }, 1000, "easeInOutExpo");
});